fun main() {
    fun part1(input: String): Int {
        val arr = input.toCharArray()
        for (i in 0..(arr.lastIndex-4)) {
            val set: HashSet<Char> = HashSet()
            for(j in 0..3) {
                set.add(arr[i + j])
            }
            if (set.size == 4) return i+4
        }
        return -1
    }

    fun part2(input: String): Int {
        val arr = input.toCharArray()
        for (i in 0..(arr.lastIndex-14)) {
            val set: HashSet<Char> = HashSet()
            for(j in 0..13) {
                set.add(arr[i + j])
            }
            if (set.size == 14) return i+14
        }
        return -1
    }

    val input = ""
//    val input = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
    println(part1(input))
    println(part2(input))
}